function formatSocialPost(content) {
  let formatted = content
    .replace(/^æ ‡é¢˜[:ï¼š]\s*/gm, '')
    .replace(/^é’©å­[:ï¼š]\s*/gm, '')
    .replace(/^CTA[:ï¼š]\s*/gm, '')
