    .replace(/^\[.*?\]\s*/gm, '')
    .replace(/<think>[\s\S]*?<\/think>/gi, '')
    .replace(/##/g, '#');

  const lines = formatted.split('\n');
  const processedLines = [];
  const hashtags = [];

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();
    if (!line) continue;

    // å¤„ç†åŒ##æ ‡ç­¾çš„è¡Œ
    if (line.match(/^##[\w\s#]+$/)) {
      const cleanLine = line.replace(/##/g, '#');
      hashtags.push(cleanLine);
    } else if (line.match(/^#[\w\s#]+$/)) {
      hashtags.push(line);
    } else if (line.includes('#') && line.split('#').length > 2) {
      const parts = line.split(/(?=#)/);
      const textPart = parts[0].trim();
      const hashtagPart = parts.slice(1).map(h => {
        let tag = h.trim();
        // æ¸…ç†åŒ#æ ‡ç­¾
        if (tag.startsWith('#')) {
          tag = tag.replace(/^#+/, '#');
        } else {
          tag = '#' + tag;
        }
        return tag;
      }).join(' ');

      if (textPart) processedLines.push(textPart);
      if (hashtagPart) hashtags.push(hashtagPart);
    } else {
      processedLines.push(line);
    }
  }

  let result = processedLines.join('\n\n');

  if (hashtags.length > 0) {
    // å†æ¬¡æ¸…ç†hashtagsä¸­çš„åŒ#æ ‡ç­¾
    const cleanHashtags = hashtags.map(tag => tag.replace(/#+/g, '#')).join(' ');
    result += '\n\n' + cleanHashtags;
  }

  return result.trim();
}

