function formatSocialPost(content) {
  let formatted = content
    .replace(/^Ê†áÈ¢ò[:Ôºö]\s*/gm, 'Title: ')
    .replace(/^Èí©Â≠ê[:Ôºö]\s*/gm, 'Hook: ')
    .replace(/^CTA[:Ôºö]\s*/gm, 'CTA: ')
    .replace(/^Ê≠£Êñá[:Ôºö]\s*/gm, 'Body: ')
    .replace(/^ÂÜÖÂÆπ[:Ôºö]\s*/gm, 'Content: ')
    .replace(/^ÊñáÊ°à[:Ôºö]\s*/gm, 'Copy: ')
    .replace(/^Call to Action[:Ôºö]\s*/gmi, 'Call to Action: ')
    .replace(/^Ë°åÂä®Âè∑Âè¨[:Ôºö]\s*/gm, 'Action Call: ')
    .replace(/^\[.*?\]\s*/gm, '')
    .replace(/<think>[\s\S]*?<\/think>/gi, '')
    .replace(/##/g, '#');

  const lines = formatted.split('\n');
  const processedLines = [];
  const allHashtags = [];

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();
    if (!line) continue;

    // Handle double ## tag lines
    if (line.match(/^##[\w\s#]+$/)) {
      const cleanLine = line.replace(/##/g, '#');
      allHashtags.push(cleanLine);
    } else if (line.match(/^#[\w\s#]+$/)) {
      allHashtags.push(line);
    } else if (line.includes('#') && line.split('#').length > 2) {
      const parts = line.split(/(?=#)/);
      const textPart = parts[0].trim();
      const hashtagPart = parts.slice(1).map(h => {
        let tag = h.trim();
        // Clean double # tags
        if (tag.startsWith('#')) {
          tag = tag.replace(/^#+/, '#');
        } else {
          tag = '#' + tag;
        }
        return tag;
      }).join(' ');

      if (textPart) processedLines.push(textPart);
      if (hashtagPart) allHashtags.push(hashtagPart);
    } else {
      processedLines.push(line);
    }
  }

  let result = processedLines.join('\n');

  if (allHashtags.length > 0) {
    // Clean double # tags in hashtags and merge all hashtags into one line
    const cleanHashtags = allHashtags.map(tag => tag.replace(/#+/g, '#').trim()).filter(tag => tag.length > 0).join(' ');
    // Only add separator when there is content to avoid empty lines
    if (result.trim()) {
      result += '\n' + cleanHashtags;
    } else {
      result = cleanHashtags;
    }
  }

  return result.trim();
}

const formattedPost = formatSocialPost(raw);

return [{ json: { 
